contracts/UniversalPayments.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title UniversalPayments
 * @dev Universal payment contract for Push Chain that handles cross-chain payments
 */
contract UniversalPayments is ReentrancyGuard, Ownable {
    
    constructor() Ownable(msg.sender) {
        // Contract deployer becomes the owner
    }
    
    struct Payment {
        address sender;
        address recipient;
        uint256 amount;
        string token;
        uint256 timestamp;
        bool isRefunded;
        string phoneNumber;
    }
    
    struct GroupPayment {
        address initiator;
        uint256 totalAmount;
        string token;
        address[] participants;
        uint256 timestamp;
        bool isSettled;
    }
    
    mapping(bytes32 => Payment) public payments;
    mapping(address => string) public phoneNumbers;
    mapping(string => address) public phoneToAddress;
    mapping(bytes32 => GroupPayment) public groupPayments;
    
    event PaymentSent(
        bytes32 indexed paymentId,
        address indexed sender,
        address indexed recipient,
        uint256 amount,
        string token,
        string phoneNumber
    );
    
    event PaymentRefunded(
        bytes32 indexed paymentId,
        address indexed sender,
        uint256 amount
    );
    
    event GroupPaymentCreated(
        bytes32 indexed groupId,
        address indexed initiator,
        uint256 totalAmount,
        uint256 participantCount
    );
    
    event PhoneNumberRegistered(
        address indexed user,
        string phoneNumber
    );
    
    /**
     * @dev Register phone number with wallet address
     */
    function registerPhoneNumber(string memory _phoneNumber) external {
        phoneNumbers[msg.sender] = _phoneNumber;
        phoneToAddress[_phoneNumber] = msg.sender;
        
        emit PhoneNumberRegistered(msg.sender, _phoneNumber);
    }
    
    /**
     * @dev Send universal payment to phone number
     */
    function sendPayment(
        string memory _recipientPhone,
        uint256 _amount,
        string memory _token
    ) external payable nonReentrant {
        require(_amount > 0, "Amount must be greater than 0");
        require(bytes(_recipientPhone).length > 0, "Recipient phone required");
        
        address recipient = phoneToAddress[_recipientPhone];
        require(recipient != address(0), "Recipient not registered");
        
        bytes32 paymentId = keccak256(
            abi.encodePacked(msg.sender, recipient, _amount, block.timestamp)
        );
        
        payments[paymentId] = Payment({
            sender: msg.sender,
            recipient: recipient,
            amount: _amount,
            token: _token,
            timestamp: block.timestamp,
            isRefunded: false,
            phoneNumber: _recipientPhone
        });
        
        // Transfer funds (simplified - would handle different tokens)
        if (msg.value > 0) {
            payable(recipient).transfer(msg.value);
        }
        
        emit PaymentSent(paymentId, msg.sender, recipient, _amount, _token, _recipientPhone);
    }
    
    /**
     * @dev Refund a payment
     */
    function refundPayment(bytes32 _paymentId) external nonReentrant {
        Payment storage payment = payments[_paymentId];
        require(payment.sender == msg.sender, "Only sender can refund");
        require(!payment.isRefunded, "Payment already refunded");
        require(block.timestamp <= payment.timestamp + 24 hours, "Refund period expired");
        
        payment.isRefunded = true;
        
        // Transfer funds back (simplified)
        payable(msg.sender).transfer(payment.amount);
        
        emit PaymentRefunded(_paymentId, msg.sender, payment.amount);
    }
    
    /**
     * @dev Create group payment for splitting expenses
     */
    function createGroupPayment(
        uint256 _totalAmount,
        string memory _token,
        address[] memory _participants
    ) external payable nonReentrant {
        require(_totalAmount > 0, "Amount must be greater than 0");
        require(_participants.length > 0, "Participants required");
        
        bytes32 groupId = keccak256(
            abi.encodePacked(msg.sender, _totalAmount, block.timestamp)
        );
        
        groupPayments[groupId] = GroupPayment({
            initiator: msg.sender,
            totalAmount: _totalAmount,
            token: _token,
            participants: _participants,
            timestamp: block.timestamp,
            isSettled: false
        });
        
        emit GroupPaymentCreated(groupId, msg.sender, _totalAmount, _participants.length);
    }
    
    /**
     * @dev Get payment details
     */
    function getPayment(bytes32 _paymentId) external view returns (Payment memory) {
        return payments[_paymentId];
    }
    
    /**
     * @dev Get user's phone number
     */
    function getPhoneNumber(address _user) external view returns (string memory) {
        return phoneNumbers[_user];
    }
    
    /**
     * @dev Get address from phone number
     */
    function getAddressFromPhone(string memory _phoneNumber) external view returns (address) {
        return phoneToAddress[_phoneNumber];
    }
}

